    /**
     * Test method for
     * {@link calculator.client.parser.CalculatorListParser#parse(String)}.
     */
    @Test
    public final void testCalculatorListParser() {
        final CalculatorListParser testListParser = new CalculatorListParser();

        // TESTS FOR CALCULATOR LIST INPUT
        // Correct Input
        try {
            testListParser.parse(CORRECTFORMEDCALCULATORS);
        } catch (final Exception e) {
            fail("Should not fail: " + e.getMessage());
        }
        assertTrue("Test if calculator files are found in input",
                (testListParser.getCalculatorList().len() > 0));
        if (testListParser == null) {
            fail("Parser failed after succesfull parse attempt");
        }
        // Malformed Input
        try {
            testListParser.parse(MALFORMEDCALCULATORS);
            fail("Should raise an exception");
        } catch (final Exception e) {
        }
        if (testListParser == null) {
            fail("Parser failed after failed parse attempt");
        }
        assertTrue("Test no calculator files are found in input",
                (testListParser.getCalculatorList().len() == 0));

        // Semi Mallformed, but parsable input
        try {
            testListParser.parse(SEMIMALFORMEDCALCULATORS);
            fail("Should raise an exception");
        } catch (final Exception e) {
        }

        // Further testing with correct input.
        // Correct Input
        try {
            testListParser.parse(CORRECTFORMEDCALCULATORS);
        } catch (final Exception e) {
            fail("Should not fail: " + e.getMessage());
        }
        // gives a list of 7 calculators
        assertTrue("7 calculators are in correct formed calculator strings",
                testListParser.getCalculatorList().len() == 7);

        // Check out of bounds
        assertNull("Out of bounds should return null", testListParser
                .getCalculatorList().getDataLocation(10));
        assertNull("Out of bounds should return null", testListParser
                .getCalculatorList().getDataLocation(-10));
    }

    /**
     * Test method for {@link calculator.client.parser.Parser#parse(String)}.
     */
    @Test
    public final void testParse() {
        testParser = new Parser();

        // TESTS FOR FLOWCHART INPUTS
        // No Input
        try {
            testParser.parse("");
        } catch (final Exception e) {
            fail("Should not fail: " + e.toString());
        }
        if (testParser == null) {
            fail("Parser failed after failed parse attempt");
        }

        // Correct Input
        try {
            testParser.parse(CORRECTFORMEDXML);
        } catch (final Exception e) {
            fail("Should not fail: " + e.getMessage());
        }
        if (testParser == null) {
            fail("Parser failed after succesfull parse attempt");
        }

        // Malformed Input
        try {
            testParser.parse(MALLFORMEDXML);
            fail("Should raise an exception");
        } catch (final Exception e) {
        }
        if (testParser == null) {
            fail("Parser failed after failed parse attempt");
        }

        // Semi Mallformed, but parsable input
        try {
            testParser.parse(SEMIMALLFORMEDXML);
        } catch (final Exception e) {
            fail("Should not fail: " + e.getMessage());
        }

        // Correct Input, further testing.
        try {
            testParser.parse(CORRECTFORMEDXML);
        } catch (final Exception e) {
            fail("Should not fail: " + e.getMessage());
        }
        if (testParser == null) {
            fail("Parser failed after succesfull parse attempt");
        }

    }

    /**
     * Test method for
     * {@link calculator.client.parser.yEdParser#getCalculator(String)}.
     */
    @Test
    public final void testyEdParser() {
        final yEdParser testYEdParser = new yEdParser();
        Question tmpQ = null;

        // TESTS FOR CALCULATOR LIST INPUT
        // No Input
        try {
            tmpQ = testYEdParser.getCalculator("");
        } catch (final Exception e) {
            fail("Should not fail: " + e.getMessage());
        }
        assertNull("No Question should be outputted", tmpQ);
        if (testYEdParser == null) {
            fail("Parser failed after succesfull parse attempt");
        }
        // Correct Input
        tmpQ = null;
        try {
            testYEdParser.getCalculator(CORRECTFORMEDyEDFile);
        } catch (final Exception e) {
            fail("Should not fail: " + e.getMessage());
        }
        if (testYEdParser == null) {
            fail("Parser failed after succesfull parse attempt");
        }
        // Malformed Input
        try {
            testYEdParser.getCalculator(MALFORMEDCALCULATORS);
            fail("Should raise an exception");
        } catch (final Exception e) {
        }
        if (testYEdParser == null) {
            fail("Parser failed after failed parse attempt");
        }

        // Semi Mallformed, but parsable input
        try {
            testYEdParser.getCalculator(SEMIMALFORMEDCALCULATORS);
            fail("Should raise an exception");
        } catch (final Exception e) {
        }

        // Continue with more intense testing base on correct formed yEd File
        try {
            tmpQ = testYEdParser.getCalculator(CORRECTFORMEDyEDFile);
            assertTrue("Questions should have been detected", tmpQ.toXML()
                    .length() > 0);
            assertTrue(
                    "First Question check failed",
                    tmpQ
                            .getQuestion()
                            .equals(
                                    "Into which of the following categories does the examined item fall? (1)"));
            assertTrue("First Question should have answers", tmpQ.getAnswers()
                    .size() > 0);
            assertFalse("Answers should have Questions", tmpQ.getAnswers().get(
                    0).getRedirect().getQuestion().isEmpty());
        } catch (final Exception e) {
            fail("Should not fail, repitition of succeeded tests.");
        }

    }

